import React, { Component } from 'react'
import TodoDataService from '../../api/todo/TodoDataService.js'
import AuthenticationService from './AuthenticationService.js'
import moment from 'moment'

class ViewTransactionsComponent extends Component {
    constructor(props) {
        console.log('constructor')
        super(props)
        this.state = {
            transactions: []
        }
        this.refreshTransactions = this.refreshTransactions.bind(this)
    }

    refreshTransactions() {
        let username = AuthenticationService.getLoggedInUserName()
        TodoDataService.retrieveAllTodos(username)
            .then(
                response => {
                    //console.log(response);
                    this.setState({ todos: response.data })
                }
            )
    }

    render() {
        console.log('render')
        let username = AuthenticationService.getLoggedInUserName()
        return (
            <div>
                <h1>Transactions of {username}</h1>
                <div className="container">
                    <table className="table">
                        <thead>
                            <tr>
                                <th>Transfer To/From</th>
                                <th>Transaction Date</th>
                                <th>Is Pending?</th>
                                <th>Amount</th>
                            </tr>
                        </thead>
                        <tbody>
                            {
                                this.state.todos.map(
                                    todo =>
                                        <tr key={transactions.id}>
                                            <td>{transactions.user2}</td>
                                            <td>{moment(transactions.date).format('YYYY-MM-DD')}</td>
                                            <td>{transactions.pending.toString()}</td>
                                            <td>{transactions.amount}</td>
                                        </tr>
                                )
                            }
                        </tbody>
                    </table>
                    <div className="row">
                        <button className="btn btn-success" onClick={this.addTodoClicked}>Add</button>
                    </div>
                </div>
            </div>
        )
    }
}

export default ViewTransactionsComponent
